package com.chefAndInequality;

public class PatternDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		System.out.println("kkkkkk");
		for (int i = 5; i >= 1; i--) {
			for (int j = 1; j <=i; j++) {
				System.out.print("*");
			}
			System.out.println("o");
		}

	}

}
